(
  async () => {

    // *** User code start ***
    const robot = globalVariables.robot;
    const operatingMode = globalVariables.operatingMode;
    let host;
    let port;

    if(operatingMode === 'simulation'){
      host = globalVariables.simHost;
      port = globalVariables.simPort;
    } else {
      // Default is realMachine
      host = globalVariables.host;
      port = 8001;
    }

    if (!robot || !host) {
      throw new Error('Robot is not created!');
    }

    const sleep = (delay) => new Promise((resolve) => setTimeout(resolve, delay));

    let inited = false;
    let initStatus = 0; // 0 - Pending, 1 - timeout, 2 - err

    const control_svr_start = async () => {
      // node-fetch
      const response = await fetch(`http${operatingMode === 'simulation' ? 's' : ''}://${host}:${port}/robot/sdk_ctrl/start`);

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      let result = '';
      inited = false;

      const resultDecoder = new TextDecoder();
      response.body.on('data', (chunk) => {
        result += resultDecoder.decode(chunk);

        if (result.includes("init!")) {
          inited = true;
          console.log("Luban: Init finished!");
          response.body.destroy(); // end
        }
      });

      response.body.on('end', () => {
        inited = true;
        console.log('Done!');
        return inited;
      });

      response.body.on('error', () => {
        initStatus = 2;
      });

      setTimeout(() => {
        initStatus = 1;
      }, 30000);
      while (!inited && initStatus == 0) {
        await sleep(100);
      };

      if (!inited) {
        throw new Error("Could not start the control server!");
      }
    };

    // Start control program
    try {
      await control_svr_start();
    } catch (error) {
      console.error(error);
      throw error;
    }

    if (!inited) {
      throw new Error("Could not initialize to the robot!");
    }

    return {
      done: true,
    }
    // *** User code end ***
  }
)()

