    const host_value = options.host;
    let operatingMode = options.operatingMode;


    if(operatingMode === 'simulation'){
      globalVariables.operatingMode = operatingMode;
      globalVariables.simHost = options.simHost;
      globalVariables.simPort = options.simPort;
    } else if(operatingMode === 'realMachine'){
      globalVariables.operatingMode = operatingMode;
    } else {
      // Not configured, follow previous behavior
      operatingMode = globalVariables.operatingMode;
    }

    const Motor = rocsclient.Motor;
    let motor;
    if(operatingMode === 'simulation'){
      motor = new motor({ host: globalVariables.simHost, port: globalVariables.simPort, ssl: true});
    } else {
      // Default is realMachine
      motor = new motor({ host: host_value });
    }
    let initStatus = 0; // 0 - Pending, 1 - timeout, 2 - err

    let connected = false;
    motor.on_connected(() => {
      console.log("Connected to the robot motor controller!");
      connected = true;
    })

    motor.on_error((err) => {
      initStatus = 2;
      console.error(err);
    });


    const sleep = (delay) => new Promise((resolve) => setTimeout(resolve, delay));

    setTimeout(() => {
      initStatus = 1;
    }, 5000);
    // Wait until either the robot is connected or timeout occurs
    while (!connected && initStatus == 0) {
      await sleep(100);
    };
    if (!connected) {
      throw new Error("Could not connect to the robot motor controller!");
    }

    // Put into globalVariables
    globalVariables.motor = motor;
    globalVariables.host = host_value;

    // Wait for motors limit ready
    let timeout = 0;

    while (timeout++ < 5 && !(motor.motor_limits)) {
      await sleep(500);
    }

    let arm_motor = motor.motor_limits.slice(0, 17);
    arm_motor = (motor.motor_limits.length > 18) ? arm_motor : [];

    let clamping_jaw = motor.motor_limits.slice(17, 19);
    clamping_jaw = (motor.motor_limits.length > 20) ? clamping_jaw : [];

    let dexterous_hand = motor.motor_limits.slice(19, 31);
    dexterous_hand = (motor.motor_limits.length > 32) ? dexterous_hand : [];

    const motors = arm_motor.concat(clamping_jaw);
    globalVariables.motors = motors;

    const smoothMotionByInterpolation = async (no, orientation, targetAngle, offset = 0.05, interval = 0.004) => {
      let motor = globalVariables.motor;
      // Use differential to move the motor smoothly
      if (Number.parseInt(no) > 8) {
        console.log('Motor number greater than 8 is not supported.');
        return;
      }

      const waitTargetDone = async (relTol = 2) => {
        const startTime = Date.now(); // capture the time at the start
        const timeout = 100; // set the timeout to 100 ms

        while (true) {

          try {
            let result = await motor.get_motor_pvc(no, orientation);
            let p = result.data?.data?.position;
            if (Math.abs(p - targetAngle) <= relTol) {
              break;
            }
            // If more than 100 ms has passed, exit the loop by log an error
            if ((Date.now() - startTime) > timeout) {
              throw new Error(`Timeout: More than ${timeout} ms passed without reaching target ${targetAngle} while current is ${p}`);
            }
          } catch (e) {
            console.log(`waitTargetDone err: ${e}`);
            break;
          }
        }
      }

      let currentPosition;

      let timeout = 0;

      while (timeout++ < 3) {
        try {
          let result = await motor.get_motor_pvc(no, orientation);
          currentPosition = result.data?.data?.position;
          if (currentPosition !== undefined) {
            break;
          }
        } catch (e) {
          console.log(`Get current position err: ${e}`);
        }
      }

      if (currentPosition !== undefined) {

        let targetPosition = targetAngle;
        let cycle = Math.abs(Math.floor((targetPosition - currentPosition) / offset));

        for (let i = 0; i < cycle; i++) {
          if (targetPosition > currentPosition) {
            currentPosition += offset;
          } else {
            currentPosition -= offset;
          }
          motor.move_motor(no, orientation, currentPosition);
          await new Promise(resolve => setTimeout(resolve, interval * 1000));
        }
        await waitTargetDone();
      } else {
        throw new Error('Cannot get current position');
      }
    }

    // Set smoothMotionByInterpolation as a global function
    globalVariables.smoothMotionByInterpolation = smoothMotionByInterpolation;

    return {
      done: true,
    }
    // *** User code end ***
  }
)()

