(async () => {
  const handleStream = async (responseBody, eventQueueName = 'llmEvents') => {
    const timestamp = Date.now();
    const decoder = new TextDecoder('utf-8');
    let isFirstEvent = true;
    let buffer = '';
    let partialElement = false;
    let talkProcessedIndex = 0;
    let talkBuffer = '';
    const punctuations = ".!?,;:\n";

    const addEvent = (name, parameters) => {
      const event = { name, timestamp, parameters };
      if (!flowInstanceVariables[eventQueueName]) {
        flowInstanceVariables[eventQueueName] = [];
      }
      flowInstanceVariables[eventQueueName].push(event);
      isFirstEvent = false;
    };

    const processTalkBuffer = (words) => {
      talkBuffer = words;
      const talkPartialBuffer = words.substring(talkProcessedIndex);

      if(partialElement){
        // Partial element is still in progress, split it.
        let lastPunctuationIndex = -1;
        for (const punctuation of punctuations) {
          const index = talkPartialBuffer.lastIndexOf(punctuation);
          lastPunctuationIndex = Math.max(lastPunctuationIndex, index);
        }

        if (lastPunctuationIndex !== -1) {
          const eventContent = talkPartialBuffer.substring(0, lastPunctuationIndex + 1);
          talkProcessedIndex += eventContent.length;
          addEvent('talk', { words: eventContent, isEnd: !partialElement });
        }
      } else {
        addEvent('talk', { words: talkPartialBuffer, isEnd: !partialElement });
        talkBuffer = '';
        talkProcessedIndex = 0;
      }
    };

    return new Promise((resolve, reject) => {
      responseBody.on('data', (chunk) => {
        buffer += decoder.decode(chunk, { stream: true });

        const startBracket = isFirstEvent ? '' : '[';
