          jsonArray = JSON.parse(startBracket + buffer);
          partialElement = false;
          buffer = '';
        } catch (error) {
          let closeBracketIndex = buffer.length;
          while( (closeBracketIndex = buffer.lastIndexOf('}', closeBracketIndex-1))>=0){
            // Try to close the JSON object after a '}'
            const partialBuffer = buffer.substring(0, closeBracketIndex + 1);
            try {
              jsonArray = JSON.parse(startBracket + partialBuffer + ']');
              // Successfully parsed the JSON array, emit the event
              buffer = buffer.substring(closeBracketIndex + 1);
              partialElement = false;
              break;
            } catch (error) {
              // Loop through all '}'
            }
          }
